3.分隔字符串 NSString*string =@&quot;sdfsfsfsAdfsdf&quot;;
NSArray&nbsp;*array = [string&nbsp;componentsSeparatedByString:@&quot;A&quot;]; //从字符A中分隔成2个元素的数组 NSLog(@&quot;array:%@&quot;,array); //结果是adfsfsfs和dfsdf
